import{_ as e,c as t,o as i,a}from"./app.2edf4356.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"Key Signals","slug":"key-signals","link":"#key-signals","children":[]},{"level":2,"title":"Routine Monitoring","slug":"routine-monitoring","link":"#routine-monitoring","children":[]},{"level":2,"title":"Troubleshooting Playbooks","slug":"troubleshooting-playbooks","link":"#troubleshooting-playbooks","children":[{"level":3,"title":"Users Cannot Log In","slug":"users-cannot-log-in","link":"#users-cannot-log-in","children":[]},{"level":3,"title":"Metrics Not Updating","slug":"metrics-not-updating","link":"#metrics-not-updating","children":[]},{"level":3,"title":"Export Job Stuck","slug":"export-job-stuck","link":"#export-job-stuck","children":[]},{"level":3,"title":"Queue Backlog","slug":"queue-backlog","link":"#queue-backlog","children":[]}]},{"level":2,"title":"Incident Response","slug":"incident-response","link":"#incident-response","children":[]},{"level":2,"title":"Tooling","slug":"tooling","link":"#tooling","children":[]},{"level":2,"title":"Maintenance Windows","slug":"maintenance-windows","link":"#maintenance-windows","children":[]}],"relativePath":"frontend/MONITORING_TROUBLESHOOTING.md"}'),n={name:"frontend/MONITORING_TROUBLESHOOTING.md"},o=a('<p>% Monitoring &amp; Troubleshooting</p><p>Last updated: 2025-11-08</p><h2 id="key-signals" tabindex="-1">Key Signals <a class="header-anchor" href="#key-signals" aria-hidden="true">#</a></h2><table><thead><tr><th>Signal</th><th>Source</th><th>Threshold</th><th>Action</th></tr></thead><tbody><tr><td>Dashboard availability</td><td>Uptime monitor / status page</td><td>&lt; 99.9% monthly</td><td>Investigate CDN/hosting; escalate to SRE.</td></tr><tr><td>API error rate</td><td>Middleware APM</td><td>&gt; 2% for 5 min</td><td>Check recent deployments, review logs, alert backend.</td></tr><tr><td>Queue backlog</td><td>Queue metrics</td><td>&gt; 500 waiting for 15 min</td><td>Investigate worker health; consider scaling or pausing incoming jobs.</td></tr><tr><td>Export failures</td><td>Export service logs</td><td>&gt; 5 failures/hour</td><td>Validate credentials, storage quotas, and rate limits.</td></tr><tr><td>Auth failures</td><td>Security logs</td><td>Spike above baseline</td><td>Possible credential stuffing; trigger security playbook.</td></tr></tbody></table><h2 id="routine-monitoring" tabindex="-1">Routine Monitoring <a class="header-anchor" href="#routine-monitoring" aria-hidden="true">#</a></h2><ul><li>Review Overview dashboard at least twice per shift.</li><li>Track queue trends and alert thresholds via scheduled reports.</li><li>Verify WebSocket connections for live features after deploys.</li><li>Rotate API keys and monitor for unused credentials quarterly.</li></ul><h2 id="troubleshooting-playbooks" tabindex="-1">Troubleshooting Playbooks <a class="header-anchor" href="#troubleshooting-playbooks" aria-hidden="true">#</a></h2><h3 id="users-cannot-log-in" tabindex="-1">Users Cannot Log In <a class="header-anchor" href="#users-cannot-log-in" aria-hidden="true">#</a></h3><ol><li>Confirm middleware auth service is reachable (<code>/api/auth/login</code>).</li><li>Check auth service status dashboards for incidents.</li><li>Review rate limiting logs; reset lockouts if applicable.</li><li>Escalate to security if multiple suspicious attempts detected.</li></ol><h3 id="metrics-not-updating" tabindex="-1">Metrics Not Updating <a class="header-anchor" href="#metrics-not-updating" aria-hidden="true">#</a></h3><ol><li>Confirm environment selection (header &gt; Environment Selector).</li><li>Check browser console for network errors; capture screenshots.</li><li>Call <code>monitoring-api.getMetrics</code> via dev tools to verify backend response.</li><li>If backend returns stale data, notify middleware team.</li></ol><h3 id="export-job-stuck" tabindex="-1">Export Job Stuck <a class="header-anchor" href="#export-job-stuck" aria-hidden="true">#</a></h3><ol><li>Verify export request parameters (format, filters).</li><li>Review export service log stream for errors.</li><li>Check storage destination quotas (S3, blob storage).</li><li>Retry with smaller dataset; if persistent, open incident ticket.</li></ol><h3 id="queue-backlog" tabindex="-1">Queue Backlog <a class="header-anchor" href="#queue-backlog" aria-hidden="true">#</a></h3><ol><li>Inspect <code>/queue</code> page for per-queue status.</li><li>Validate worker health metrics (CPU, memory).</li><li>Retry failed jobs if safe; otherwise pause intake.</li><li>Engage backend to scale workers or investigate root cause.</li></ol><h2 id="incident-response" tabindex="-1">Incident Response <a class="header-anchor" href="#incident-response" aria-hidden="true">#</a></h2><ul><li>Create incident channel (e.g., <code>#incident-sf-dashboard</code>).</li><li>Log timeline, affected features, and remediation steps.</li><li>Update <code>docs/CHANGELOG.md</code> with post-incident summary if user-facing.</li><li>Schedule postmortem within 48 hours for high-severity incidents.</li></ul><h2 id="tooling" tabindex="-1">Tooling <a class="header-anchor" href="#tooling" aria-hidden="true">#</a></h2><ul><li><strong>Logging</strong>: Centralized log aggregation (e.g., Datadog, ELK). Use structured fields for filtering.</li><li><strong>Alerting</strong>: Pager duty/On-call rotation. Alerts should include direct links to relevant dashboard views.</li><li><strong>Dashboards</strong>: Maintain Grafana/Datadog dashboards for queue health, API latency, authentication.</li></ul><h2 id="maintenance-windows" tabindex="-1">Maintenance Windows <a class="header-anchor" href="#maintenance-windows" aria-hidden="true">#</a></h2><ul><li>Announce maintenance 24 hours ahead via support channels.</li><li>Temporarily disable exports (feature flag) if operation may cause inconsistencies.</li><li>After maintenance, run smoke tests and confirm monitoring baselines return to normal.</li></ul>',21),r=[o];function l(s,d,c,h,u,g){return i(),t("div",null,r)}const b=e(n,[["render",l]]);export{f as __pageData,b as default};
